mapper.select:
select
role_id, menu_id, del_flag, create_by, create_time, update_by, update_time
from sys_role_menu


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_role_menu


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
