mapper.select:
select
id, club_id, dept_id, del_flag, create_by, create_time, update_by, update_time
from sys_club_dept


        and id = #{id}


        and club_id = #{clubId}


        and dept_id = #{deptId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_club_dept


        and id = #{id}


        and club_id = #{clubId}


        and dept_id = #{deptId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
