mapper.select:
select
id, name, content, del_flag, create_by, create_time, update_by, update_time
from ws_wall


        and id = #{id}


        and name = #{name}


        and content = #{content}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ws_wall


        and id = #{id}


        and name = #{name}


        and content = #{content}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
